import { reduce } from 'lodash';
import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [grid, setGrid] = useState([]);
  const [algorithmOpt, setAlgorithmOpt] = useState("hidden");
  const [speedOpt, setSpeedOpt] = useState("hidden");
  const [gridOpt, setGridOpt] = useState("hidden");
  const [lastOpt, setLastOpt] = useState("hidden");
  useEffect(() => {GenerateGrid();}, []);

  function GenerateGrid() {
    let generatedPixels = [];
    for (let i = 0; i < 2000; i++) {
      if (i != 974) {
        generatedPixels.push(
          <div id="Pixel"></div>
        );
      } else {
        generatedPixels.push(
          <div id="Pixel" style={{backgroundColor: "orange"}}></div>
        );
      }
    };
    setGrid(generatedPixels);
    console.log(generatedPixels);
    return generatedPixels;
  };

  return (
    <div className="App">
      <div id="Header">
        <div id="HeaderTitle">
          Algorithms Visualized
        </div>
        <div id="Categories">
          <div class="Category" 
            onClick={() => {algorithmOpt == "hidden" ? setAlgorithmOpt() :  setAlgorithmOpt()}}>
            Algorithm
          </div>
          <div class="Category">Speed</div>
          <div class="Category">Draw</div>
          <div class="Category">Grid</div>
        </div>
        <div id="OptionsContainer">
          <div class="Options" style={{visibility: algorithmOpt}}>Algorithm</div>
          <div class="Options">Speed</div>
          <div class="Options">Algorithm</div>
          <div class="Options">Grid</div>
        </div>
      </div>
      <div id="StartPause">Start!</div>
      <div id="Key"></div>
      <div id="GridContainer">
        {grid}
      </div>
    </div>
  );
}

export default App;
