import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [grid, setGrid] = useState([]);
  const [algOpt, setAlgOpt] = useState("hidden");
  const [speedOpt, setSpeedOpt] = useState("hidden");
  const [toolsOpt, setToolsOpt] = useState("hidden");
  const [gridOpt, setGridOpt] = useState("hidden");
  useEffect(() => {GenerateGrid();}, []);


  function GenerateGrid() {
    let generatedPixels = [];
    for (let i = 0; i < 2000; i++) {
      if (i != 974) {
        generatedPixels.push(
          <div id="Pixel"></div>
        );
      } else {
        generatedPixels.push(
          <div id="Pixel" style={{backgroundColor: "orange"}}></div>
        );
      }
    };
    setGrid(generatedPixels);
    console.log(generatedPixels);
    return generatedPixels;
  };


  function ToggleCategory(state, setFunction) {
    let categories = [setAlgOpt, setSpeedOpt, setToolsOpt, setGridOpt];
    for (let index = 0; index < 4; index++) {
      categories[index]("hidden");
    };
    (state === "hidden") ? setFunction("visible") : setFunction("hidden");
  };


  return (
    <div className="App">
      <div id="Header">
        <div id="HeaderTitle">
          Algorithms Visualized
        </div>
        <div id="Categories">
          <div className="Category" onClick={() => {ToggleCategory(algOpt, setAlgOpt)}}>
            Algorithms
          </div>
          <div className="Category" onClick={() => {ToggleCategory(speedOpt, setSpeedOpt)}}>
            Speed
          </div>
          <div className="Category" onClick={() => {ToggleCategory(toolsOpt, setToolsOpt)}}>
            Tools
          </div>
          <div className="Category" onClick={() => {ToggleCategory(gridOpt, setGridOpt)}}>
            Grid
          </div>
        </div>
        <div id="OptionsContainer">
          <div className="Options" style={{visibility: algOpt}}>
            <div className="Option">

            </div>
          </div>
          <div className="Options" style={{visibility: speedOpt}}>Speed</div>
          <div className="Options" style={{visibility: toolsOpt}}>Algorithm</div>
          <div className="Options" style={{visibility: gridOpt}}>Grid</div>
        </div>
      </div>
      <div id="StartPause">Start!</div>
      <div id="Key"></div>
      <div id="GridContainer">
        {grid}
      </div>
    </div>
  );
}

export default App;
