import React, { useState, useEffect, useRef } from 'react';
import './App.css';


function App() {
  const [grid, setGrid] = useState([]);
  const [algorithm, setAlogirthm] = useState("BFS");
  const [algOpt, setAlgOpt] = useState("none");
  const [speedOpt, setSpeedOpt] = useState("none");
  const [toolsOpt, setToolsOpt] = useState("none");
  const [gridOpt, setGridOpt] = useState("none");
  const [startStop, setStartStop] = useState(["Start!", "green"]);
  const [origin, setOrigin] = useState(288);
  const [dest, setDest] = useState(800);
  const [path, setPath] = useState([]);
  const [barrier, setBarrier] = useState([]);
  const [curr, setCurr] = useState("");
  const mouseHeld = useRef("up");
  useEffect(() => {
    GenerateGrid();
  }, [origin, dest, path, barrier, curr]);
  useEffect(() => {
    if (startStop[0] === "Stop!" && algorithm === "BFS") {
      if (path.length > 0) BFS(path[path.length - 1]);
      else BFS(origin);
    };
  }, [startStop]);
  window.addEventListener('mousedown', () => {
    mouseHeld.current = "down";
  });
  window.addEventListener('mouseup', () => {
    mouseHeld.current = "up";
  });

  const sleep = ms => new Promise(
    resolve => setTimeout(resolve, ms)
  );


  function ClickDraw(index) {
    if (index !== origin && index !== dest && !path.includes(index)) {
      setBarrier(barrier => [...barrier, index]);
    };
  }


  function DragDraw(index) {
    if (index !== origin && index !== dest && !path.includes(index) && mouseHeld.current === "down") {
      setBarrier(barrier => [...barrier, index]);
    };
  };


  function EmptyPixel(pixel, pathCopy) {
    if (!barrier.includes(pixel) && !pathCopy.includes(pixel)) return true;
    else return false;
  };


  async function DFS(pixel) {
    let stack = [pixel];
    let pathCopy = path;
    while (stack.length !== 0 && stack[stack.length - 1] !== dest) {
      await sleep(50)
      let current = stack.pop()
      pathCopy.push(current);
      setPath(path => [...path, current]);
      setCurr(current);
      if (current % 33 > 0 && EmptyPixel(current - 1, pathCopy)) stack.push(current - 1);
      if (current < 1056 && EmptyPixel(current + 33, pathCopy)) stack.push(current + 33);
      if (current % 33 < 32 && EmptyPixel(current + 1, pathCopy)) stack.push(current + 1);
      if (current > 32 && EmptyPixel(current - 33, pathCopy)) stack.push(current - 33);
    };
    setCurr("");
  };


  async function BFS(pixel) {
    let queue = [pixel];
    let pathCopy = path;
    while (queue.length !== 0) {
      let len = queue.length;
      for (let index = 0; index < queue.length; index++) {
        await sleep(100)
        let current = queue.shift();
        if (current === dest) {
          setCurr("");
          return;
        }
        pathCopy.push(current);
        setPath(path => [...path, current]);
        setCurr(current);
        if (current % 33 > 0 && EmptyPixel(current - 1, pathCopy)) queue.push(current - 1);
        if (current < 1056 && EmptyPixel(current + 33, pathCopy)) queue.push(current + 33);
        if (current % 33 < 32 && EmptyPixel(current + 1, pathCopy)) queue.push(current + 1);
        if (current > 32 && EmptyPixel(current - 33, pathCopy)) queue.push(current - 33);
      }
    }
    setCurr("");
  }


  function GenerateGrid() {
    let generatedPixels = [];
    for (let index = 0; index < 1089; index++) {
        generatedPixels.push(
          <div id="Pixel" onMouseEnter={() => {DragDraw(index)}} 
            onMouseDown={() => {ClickDraw(index)}} 
            style={{backgroundColor: 
              (index === curr) ? "green" : (index === origin || path.includes(index)) ? "rgb(120, 210, 130)" : (index === dest) ? "rgb(210, 120, 130)" :
              (barrier.includes(index) ? "rgb(40, 40, 40)" : "rgb(40, 100, 140)")
            }}>
          </div>
        );
    };
    setGrid(generatedPixels);
    return generatedPixels;
  };


  function ToggleCategory(state, setFunction) {
    let categories = [setAlgOpt, setSpeedOpt, setToolsOpt, setGridOpt];
    for (let index = 0; index < 4; index++) {
      categories[index]("none");
    };
    (state === "none") ? setFunction("block") : setFunction("none");
  };


  function StartStop() {
    (startStop[0] === "Start!") ? setStartStop(["Stop!", "red"]) : setStartStop(["Start!", "green"]);
  };


  return (
    <div className="App">
      <div id="Header">
        <div id="HeaderTitle">
          Pathfinding Visualized
        </div>
        <div id="Categories">
          <div className="Category" onClick={() => {ToggleCategory(algOpt, setAlgOpt)}}>
            Algorithms
          </div>
          <div className="Category" onClick={() => {ToggleCategory(speedOpt, setSpeedOpt)}}>
            Speed
          </div>
          <div className="Category" onClick={() => {ToggleCategory(toolsOpt, setToolsOpt)}}>
            Tools
          </div>
          <div className="Category" onClick={() => {ToggleCategory(gridOpt, setGridOpt)}}>
            Grid
          </div>
        </div>
      </div>
      <div id="StartStop" onClick={() => {StartStop()}} style={{backgroundColor: startStop[1]}}>
        {startStop[0]}
      </div>
      <div id="Key"></div>
      <div id="GridContainer">
        {grid}
      </div> 
    </div>
  );
} 

export default App;
